    #[test]
    fn test_zerofier() {
        let domain = [0, 5, 2, 5, 5, 1]
            .iter()
            .map(|x: &i32| IntPG::from(*x))
            .collect();

        let res = Polynomial::zerofier(&domain);

        for x in domain {
            assert_eq!(res.evaluate(&x), IntPG::ZERO);
        }
    }
